var kendoImageUploaderSrc;
var kendoImageUploaderWindow;
function openKendoImageUploader(p) {

    kendoImageUploaderWindow = tinyMCE.activeEditor.windowManager.open({
        url : getKendoImageUploaderUrl(),
        width : 500,
        height : 130,
        title: 'ارسال تصویر'
    }, {
        custom_param : 1
    });

    kendoImageUploaderSrc = $($($(p.currentTarget).parent().context).find('input').first());
}

function imageSelected(file){
    kendoImageUploaderSrc.attr('value', file);
    kendoImageUploaderSrc.focus();
    kendoImageUploaderWindow.close();
}


