$.formUtils.addValidator({name:"ukvatnumber",validatorFunction:function(e){e=e.replace(/[^0-9]/g,"");if(e.length<9){return false}var t=false;var n=[];n=e.split("");var r=Number(n[7]+n[8]);var i=n[0];var s=n[1];if(i==0&&s>0){return false}var o=0;for(var u=0;u<7;u++){o+=n[u]*(8-u)}var a=0;var u=0;for(var f=8;f>=2;f--){a+=n[u]*f;u++}while(o>0){o-=97}o=Math.abs(o);if(r==o){t=true}if(!t){o=o%97;if(o>=55){o=o-55}else{o=o+42}if(o==r){t=true}}return t},errorMessage:"",errorMessageKey:"badUKVatAnswer"})